import React, { useEffect, useState } from 'react';
import ReactFlow, { Background, Controls, MiniMap } from 'reactflow';
import 'reactflow/dist/style.css';
import '../styles/Graph.css';
import themesData from '../data/mockThemes.json';

function Graph() {
  const [elements, setElements] = useState([]);

  useEffect(() => {
    const nodes = {};
    const edges = [];
    const nodePositions = {}; // Храним координаты узлов

    const positionNode = (id, level, index) => {
      const x = 250 + index * 300; // Отступ по оси X (для отступа между узлами)
      const y = 100 + level * 150; // Отступ по оси Y (для уровней дерева)
      return { x, y };
    };

    themesData.forEach(theme => {
      const position = positionNode(theme.id, 0, 0);
      nodes[theme.id] = {
        id: theme.id,
        data: { label: theme.name },
        position,
      };
    });

    const processNode = (theme, level) => {
      if (nodes[theme.id]) {
        if (theme.leftNode && nodes[theme.leftNode]) {
          edges.push({
            id: `e${theme.id}-${theme.leftNode}`,
            source: theme.id,
            target: theme.leftNode,
            animated: true,
          });
          processNode({ id: theme.leftNode, leftNode: null, rightNode: null }, level + 1); // Рекурсивно обрабатываем левую ноду
        }
        if (theme.rightNode && nodes[theme.rightNode]) {
          edges.push({
            id: `e${theme.id}-${theme.rightNode}`,
            source: theme.id,
            target: theme.rightNode,
            animated: true,
          });
          processNode({ id: theme.rightNode, leftNode: null, rightNode: null }, level + 1); // Рекурсивно обрабатываем правую ноду
        }
      }
    };

    themesData.forEach((theme, index) => {
      const position = positionNode(theme.id, 0, index);
      nodes[theme.id] = {
        id: theme.id,
        data: { label: theme.name },
        position,
      };
    });

    themesData.forEach(theme => {
      processNode(theme, 0);
    });

    setElements([
      ...Object.values(nodes),
      ...edges,
    ]);
  }, []);

  return (
    <div className="graph-container">
      <ReactFlow elements={elements}>
        <Background />
        <Controls />
        <MiniMap />
      </ReactFlow>
    </div>
  );
}

export default Graph;
