import React, { useState, useCallback, useEffect } from 'react';
import ReactFlow, {
  Background,
  Controls,
  MiniMap,
  useNodesState,
  useEdgesState,
} from 'reactflow';
import 'reactflow/dist/style.css';
import '../styles/Graph.css';
import '../styles/ThemePanel.css';

import mockGraph from '../data/mockThemes.json';
import ThemePanel from './ThemePanel';

export default function Graph() {
  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);
  const [selectedNode, setSelectedNode] = useState(null);
  const [isPanelOpen, setIsPanelOpen] = useState(false);
  const [language, setLanguage] = useState('ru'); // Состояние для языка

  const handleNodeClick = (event, node) => {
    setSelectedNode(node);
    setIsPanelOpen(true);
    const query = node.data.label; // Возьмем название темы как запрос
    fetchCustomSearchResults(query); // Выполним запрос с языком
  };

  const fetchCustomSearchResults = async (query) => {
    try {
      const results = await getCustomSearchResults(query, language); // Передаем язык в запрос
      console.log(results); // Здесь можно отобразить результаты
    } catch (error) {
      console.error('Error fetching search results:', error);
    }
  };

  const getCustomSearchResults = async (query, lang) => {
    const response = await fetch(`http://localhost:8080/v1/customsearch?q=${query}&lang=${lang}`);
    if (!response.ok) {
      throw new Error('Failed to fetch custom search results');
    }
    const data = await response.json();
    return data.results; // Возвращаем результаты поиска
  };

  const handlePaneClick = () => {
    if (isPanelOpen) {
      setIsPanelOpen(false);
      setSelectedNode(null);
    }
  };

  const importGraph = async (event) => {
    const file = event.target.files[0];
    if (file) {
      const text = await file.text();
      const { nodes: importedNodes, edges: importedEdges } = JSON.parse(text);
      setNodes(importedNodes.map(node => ({ ...node, draggable: false })));
      setEdges(importedEdges);
    }
  };

  useEffect(() => {
    setNodes(mockGraph.nodes.map(node => ({ ...node, draggable: false })));
    setEdges(mockGraph.edges || []);
  }, []);

  const closePanel = () => {
    setIsPanelOpen(false);
    setSelectedNode(null);
  };

  return (
    <div style={{ height: '100vh', position: 'relative', backgroundColor: '#ffffff' }}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        onNodeClick={handleNodeClick}
        onPaneClick={handlePaneClick}
        fitView
        connectable={false} // Отключаем добавление соединений
        defaultViewport={{ x: 0, y: 0, zoom: 0.01 }}
        proOptions={{ hideAttribution: true }} // Убираем лого React Flow
      >
        <Background color="#e0e0e0" gap={20} variant="dots" />
        <MiniMap />
        {/* Оставляем только те элементы управления, которые нужны */}
        <Controls showInteractive={false} />
      </ReactFlow>
      {isPanelOpen && selectedNode && (
        <ThemePanel theme={selectedNode.data} onClose={closePanel} />
      )}
    </div>
  );
}
