import React, { useState } from 'react';
import Header from './components/Header';
import Footer from './components/Footer';
import Graph from './components/Graph';
import ThemePanel from './components/ThemePanel';
import TopicList from './components/TopicList';
import themesData from './data/mockThemes.json';
import './styles/App.css';

function App() {
  const [selectedTheme, setSelectedTheme] = useState(null);

  const handleThemeClick = (theme) => {
    setSelectedTheme(theme);
  };

  return (
    <div className="app-container">
      <Header />
      <div className="main-content">
        <div className="sidebar">
          <TopicList themes={themesData} onThemeClick={handleThemeClick} />
        </div>
        <div className="content">
          <Graph themes={themesData} onThemeClick={handleThemeClick} />
          {selectedTheme && (
            <ThemePanel theme={selectedTheme} onClose={() => setSelectedTheme(null)} />
          )}
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default App;
