import React, { useState, useCallback } from 'react';
import ReactFlow, { Background, Controls, MiniMap, addEdge, useNodesState, useEdgesState } from 'reactflow';
import 'reactflow/dist/style.css';

const initialNodes = [
  { id: '1', data: { label: 'Theme 1' }, position: { x: 0, y: 0 } },
  { id: '2', data: { label: 'Theme 2' }, position: { x: 150, y: 150 } },
  { id: '3', data: { label: 'Theme 3' }, position: { x: 300, y: 300 } },
  { id: '4', data: { label: 'Theme 4' }, position: { x: 450, y: 450 } },
];

const initialEdges = [
  { id: '1-2', source: '1', target: '2', animated: true },
  { id: '2-3', source: '2', target: '3' },
  { id: '3-4', source: '3', target: '4', animated: true },
];

export default function Graph() {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
  const [editingNode, setEditingNode] = useState(null);
  const [newLabel, setNewLabel] = useState('');

  const onConnect = useCallback(
    (connection) => setEdges((edges) => addEdge(connection, edges)),
    [setEdges]
  );

  const onNodeDoubleClick = (event, node) => {
    setEditingNode(node.id);
    setNewLabel(node.data.label);
  };

  const handleSave = () => {
    setNodes((nds) =>
      nds.map((node) =>
        node.id === editingNode ? { ...node, data: { ...node.data, label: newLabel } } : node
      )
    );
    setEditingNode(null);
  };

  const handleCancel = () => {
    setEditingNode(null);
  };

  return (
    <div style={{ height: '100vh' }}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        onConnect={onConnect}
        onNodeDoubleClick={onNodeDoubleClick}
        fitView
      >
        <Background />
        <MiniMap />
        <Controls />
      </ReactFlow>
      {editingNode && (
        <div
          style={{
            position: 'absolute',
            top: '10%',
            left: '10%',
            backgroundColor: 'white',
            padding: '20px',
            boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.2)',
          }}
        >
          <h3>Edit Node</h3>
          <input
            type="text"
            value={newLabel}
            onChange={(e) => setNewLabel(e.target.value)}
          />
          <button onClick={handleSave}>Save</button>
          <button onClick={handleCancel}>Cancel</button>
        </div>
      )}
    </div>
  );
}
