import React, { useEffect, useState } from 'react';
import ReactFlow, { Background, Controls, MiniMap, ReactFlowProvider } from 'reactflow';
import 'reactflow/dist/style.css';
import '../styles/Graph.css';
import themesData from '../data/mockThemes.json';

function Graph() {
  const [elements, setElements] = useState([]);

  const getNodePosition = (level, index) => ({
    x: index * 250 + 100, // Adjust starting point horizontally for spacing
    y: level * 150 + 100, // Adjust vertical distance between levels
  });

  const buildGraphElements = (themes) => {
    const nodes = [];
    const edges = [];
    const processedNodes = new Set();
    let index = 0;

    const processNode = (nodeId, level) => {
      if (processedNodes.has(nodeId)) return;
      processedNodes.add(nodeId);

      const theme = themes.find((t) => t.id === nodeId);
      if (!theme) return;

      const position = getNodePosition(level, index);
      nodes.push({
        id: theme.id,
        data: { label: theme.name },
        position,
        style: { borderRadius: 10, padding: 10, background: '#00bcd4', color: 'white' },
      });

      if (theme.leftNode) {
        edges.push({
          id: `e${theme.id}-${theme.leftNode}`,
          source: theme.id,
          target: theme.leftNode,
          animated: true,
          style: { stroke: '#00bcd4' },
        });
        processNode(theme.leftNode, level + 1);
      }

      if (theme.rightNode) {
        edges.push({
          id: `e${theme.id}-${theme.rightNode}`,
          source: theme.id,
          target: theme.rightNode,
          animated: true,
          style: { stroke: '#00bcd4' },
        });
        processNode(theme.rightNode, level + 1);
      }

      index++;
    };

    const rootNode = themes.find((theme) => theme.id == 1);
    if (rootNode) processNode(rootNode.id, 0);

    return { nodes, edges };
  };

  useEffect(() => {
    debugger;
    const { nodes, edges } = buildGraphElements(themesData);
    setElements([...nodes, ...edges]);
    console.log(themesData);
  }, []);

  return (
    <ReactFlowProvider>
      <div style={{ height: '100vh' }}>
        <ReactFlow
          elements={elements}
          style={{ width: '100%', height: '100%' }}
        >
          <Background />
          <Controls />
          <MiniMap />
        </ReactFlow>
      </div>
    </ReactFlowProvider>
  );
}

export default Graph;
