import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import Footer from './components/Footer';
import Graph from './components/Graph';
import SectionList from './components/SectionList';
import { fetchRoadmaps } from './services/api'; // Импортируем API-запрос
import './styles/App.css';

function App() {
  const [sections, setSections] = useState([]);
  const [selectedSection, setSelectedSection] = useState(null);

  useEffect(() => {
    async function loadRoadmaps() {
      const roadmaps = await fetchRoadmaps();
      setSections(roadmaps);
    }

    loadRoadmaps();
  }, []);

  const handleSectionClick = (section) => {
    setSelectedSection(section);
  };

  return (
    <div className="app-container">
      <Header />
      <div className="main-content">
        <SectionList sections={sections} onSectionClick={handleSectionClick} />
        <div className="content">
          {selectedSection && <Graph section={selectedSection} />}
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default App;
