import React, { useEffect, useState } from 'react';
import ReactFlow, { Background, Controls, MiniMap, ReactFlowProvider } from 'reactflow';
import 'reactflow/dist/style.css';
import '../styles/Graph.css';
import themesData from '../data/mockThemes.json';

function Graph() {
  const [elements, setElements] = useState([]);

  const getNodePosition = (level, index, spacing = 300) => ({
    x: index * spacing,
    y: level * 200,
  });

  const buildGraphElements = (themes) => {
    const nodes = [];
    const edges = [];
    const processedNodes = new Set();

    const processNode = (nodeId, level, index) => {
      if (processedNodes.has(nodeId)) return;
      processedNodes.add(nodeId);

      const theme = themes.find((t) => t.id === nodeId);
      if (!theme) return;

      const position = getNodePosition(level, index);
      nodes.push({
        id: theme.id,
        data: { label: theme.name },
        position,
      });

      if (theme.leftNode) {
        edges.push({
          id: `e${theme.id}-${theme.leftNode}`,
          source: theme.id,
          target: theme.leftNode,
          animated: true,
        });
        processNode(theme.leftNode, level + 1, index - 1);
      }

      if (theme.rightNode) {
        edges.push({
          id: `e${theme.id}-${theme.rightNode}`,
          source: theme.id,
          target: theme.rightNode,
          animated: true,
        });
        processNode(theme.rightNode, level + 1, index + 1);
      }
    };

    const rootNode = themes.find((theme) => !theme.leftNode && !theme.rightNode);
    if (rootNode) processNode(rootNode.id, 0, 0);

    return {
      nodes,
      edges,
    };
  };

  useEffect(() => {
    const { nodes, edges } = buildGraphElements(themesData);
    setElements([...nodes, ...edges]);
  }, []);

  return (
    <ReactFlowProvider>
      <div className="graph-container">
        <ReactFlow
          elements={elements}
          style={{ width: '100%', height: '100vh' }}
          nodeTypes={{ default: 'reactflow__node' }}
        >
          <Background />
          <Controls />
          <MiniMap />
        </ReactFlow>
      </div>
    </ReactFlowProvider>
  );
}

export default Graph;
