import React, { useState } from 'react';
import Header from './components/Header';
import Footer from './components/Footer';
import Graph from './components/Graph';
import ThemePanel from './components/ThemePanel';
import SectionList from './components/SectionList';
import './styles/App.css';

function App() {
  const [sections, setSections] = useState([]);
  const [selectedGraph, setSelectedGraph] = useState(null);

  const fetchSections = async () => {
    const response = await fetch('http://localhost:8080/v1/roadmaps');
    const data = await response.json();
    setSections(data.roadmaps); // Сохраняем секции с id
  };

  // Загружаем секции при монтировании компонента
  useEffect(() => {
    fetchSections();
  }, []);

  const handleSectionClick = async (sectionId) => {
    const response = await fetch(`http://localhost:8080/v1/roadmaps/${sectionId}`);
    const data = await response.json();
    setSelectedGraph(data.graph); // Сохраняем данные графа
  };

  return (
    <div className="app-container">
      <Header />
      <div className="main-content">
        <SectionList sections={sections} onSectionClick={handleSectionClick} />
        <div className="content">
          {selectedGraph && (
            <Graph graph={selectedGraph} />
          )}
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default App;
