import React, { useState } from 'react';
import '../styles/SectionList.css';

function SectionList({ sections, onSectionClick, onLanguageChange }) {
  const [isCollapsed, setIsCollapsed] = useState(false);
  const [selectedLanguage, setSelectedLanguage] = useState('ru'); // По умолчанию русский

  const toggleCollapse = () => {
    setIsCollapsed(!isCollapsed);
  };

  const handleLanguageChange = (event) => {
    const language = event.target.value;
    setSelectedLanguage(language);
    onLanguageChange(language); // Передаем выбранный язык в родительский компонент
  };

  return (
    <div className={`section-list ${isCollapsed ? 'collapsed' : ''}`}>
      <div className="collapse-toggle" onClick={toggleCollapse}>
        {isCollapsed ? '▶' : '✖'}
      </div>
      {!isCollapsed && (
        <div className="section-list-content">
          <h3>Sections</h3>
          <ul>
            {sections.map((section) => (
              <li key={section.id} onClick={() => onSectionClick(section.id)}>
                {section.technology}
              </li>
            ))}
          </ul>
        </div>
      )}
      {/* Добавляем блок для выбора языка */}
      <div className="language-selector">
        <p>Язык ответов:</p>
        <select value={selectedLanguage} onChange={handleLanguageChange}>
          <option value="ru">Русский</option>
          <option value="en">English</option>
        </select>
      </div>
    </div>
  );
}

export default SectionList;
