  export const getSections = async () => {
    const response = await fetch('http://localhost:8080/v1/roadmaps');
    if (!response.ok) {
      throw new Error('Failed to fetch sections');
    }
    const data = await response.json();
    return data.roadmaps; // Возвращаем список секций
  };
  

  export const getGraphBySectionId = async (sectionId) => {
    const response = await fetch(`http://localhost:8080/v1/roadmaps/${sectionId}`);
    if (!response.ok) {
      throw new Error('Failed to fetch graph data');
    }
    const data = await response.json();
    return data.graph; // Возвращаем граф
  };
