import React, { useState } from 'react';
import '../styles/SectionList.css';

function SectionList({ sections, onSectionClick }) {
  const [isCollapsed, setIsCollapsed] = useState(false);

  const toggleCollapse = () => {
    setIsCollapsed(!isCollapsed);
  };

  return (
    <div className={`section-list ${isCollapsed ? 'collapsed' : ''}`}>
      <div className="collapse-toggle" onClick={toggleCollapse}>
        {isCollapsed ? '▶' : '✖'}
      </div>
      {!isCollapsed && (
        <div className="section-list-content">
          <h3>Sections</h3>
          <ul>
            {sections.map((section) => (
              <li key={section.id} onClick={() => onSectionClick(section)}>
                {section.name}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default SectionList;
