import React, { useState, useEffect } from 'react';
import ReactFlow, {
  Background,
  Controls,
  MiniMap,
  useNodesState,
  useEdgesState,
} from 'reactflow';
import 'reactflow/dist/style.css';
import '../styles/Graph.css';
import '../styles/ThemePanel.css';

import ThemePanel from './ThemePanel';
import { getCustomSearchResults } from '../services/api';

export default function Graph({ graphs, section, language }) {
  const [currentGraphIndex, setCurrentGraphIndex] = useState(0); // Индекс выбранного графа
  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);
  const [selectedNode, setSelectedNode] = useState(null);
  const [isPanelOpen, setIsPanelOpen] = useState(false);
  const [searchResults, setSearchResults] = useState([]);

  useEffect(() => {
    if (graphs && graphs[currentGraphIndex]) {
      const graph = graphs[currentGraphIndex];
      setNodes(graph.nodes.map(node => ({ ...node, draggable: false })));
      setEdges(graph.edges || []);
    }
  }, [graphs, currentGraphIndex]);

  const handleNodeClick = (event, node) => {
    setSelectedNode(node);
    setIsPanelOpen(true);
    fetchCustomSearchResults(node.data.label, section, language);
  };

  const fetchCustomSearchResults = async (topic, technology, language) => {
    try {
      const results = await getCustomSearchResults(topic, technology, language);
      setSearchResults(results);
    } catch (error) {
      console.error('Error fetching search results:', error);
    }
  };

  const handlePaneClick = () => {
    if (isPanelOpen) {
      setIsPanelOpen(false);
      setSelectedNode(null);
    }
  };

  const closePanel = () => {
    setIsPanelOpen(false);
    setSelectedNode(null);
  };

  const switchGraph = (index) => {
    setCurrentGraphIndex(index); // Переключаемся на другой граф
    setSelectedNode(null); // Сбрасываем выбранный узел
    setIsPanelOpen(false); // Закрываем панель
  };

  return (
    <div style={{ height: '100vh', position: 'relative', backgroundColor: '#ffffff' }}>
      {/* Панель для выбора графа */}
      <div className="graph-selector">
        {graphs.map((_, index) => (
          <button
            key={index}
            onClick={() => switchGraph(index)}
            disabled={index === currentGraphIndex}
          >
            Graph {index + 1}
          </button>
        ))}
      </div>

      {/* Отображение текущего графа */}
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        onNodeClick={handleNodeClick}
        onPaneClick={handlePaneClick}
        fitView
        connectable={false}
        defaultViewport={{ x: 0, y: 0, zoom: 0.01 }}
        proOptions={{ hideAttribution: true }}
      >
        <Background color="#e0e0e0" gap={20} variant="dots" />
        <MiniMap />
        <Controls showInteractive={false} />
      </ReactFlow>

      {isPanelOpen && selectedNode && (
        <ThemePanel theme={selectedNode.data} onClose={closePanel} searchResults={searchResults} />
      )}
    </div>
  );
}
