export const getSections = async () => {
    const response = await fetch('http://localhost:8080/v1/roadmaps');
    if (!response.ok) {
      throw new Error('Failed to fetch sections');
    }
    const data = await response.json();
    return data.roadmaps; // Возвращаем список секций
};
  

export const getGraphBySectionId = async (sectionId) => {
    const response = await fetch(`http://localhost:8080/v1/roadmaps/${sectionId}`);
    if (!response.ok) {
      throw new Error('Failed to fetch graph data');
    }
    const data = await response.json();
    return data.graph; // Возвращаем граф
};


export const getGoogleSearchResults = async (query) => {
    const apiKey = 'AIzaSyAQzNT3gdf2C7d0cHRDFQk2t4yjJ1vmPvA'; // Ваш API-ключ
    const cx = '017576662512468239146:omuauf_lfve'; // Ваш cx (Custom Search Engine ID)
    
    const url = `https://www.googleapis.com/customsearch/v1?key=${apiKey}&cx=${cx}&q=${query}`;
    
    try {
      const response = await fetch(url);
      
      if (!response.ok) {
        throw new Error('Failed to fetch Google search results');
      }
      
      const data = await response.json();
      return data.items; // Возвращаем массив результатов поиска
    } catch (error) {
      console.error('Error fetching Google search results:', error);
      throw error;
    }
};
