export async function fetchRoadmaps() {
    try {
      const response = await fetch('http://localhost:8080/v1/roadmaps');
      if (!response.ok) {
        throw new Error(`Error fetching roadmaps: ${response.statusText}`);
      }
      const data = await response.json();
      return data.roadmaps;
    } catch (error) {
      console.error(error);
      return [];
    }
  }
